import Header from "./components/todoList/Header";
import TodoList from "./components/todoList/TodoList";
import { useEffect, useState } from "react";
import {link} from "./localhost";
import {createNewTodo} from "./services"
function App() {

  const fetchData = async () => {
    const response = await fetch(link);
    setTodos(await response.json());
  };

  useEffect(() => {
    fetchData(setTodos);
  }, []);

  const [todos, setTodos] = useState([
  ]);
  function listItemChangeAPI(elem) {
    return fetch(`${link}/toggle/${elem.id}`, {
      method: "PUT",
    }).then(fetchData);
  }
  function deleteItemAPI(todo) {
    return fetch(`${link}${todo.id}`, {
      method: "DELETE",
    }).then(fetchData);
  }
  function deleteAllItemsAPI() {
    return fetch(link, {
      method: "DELETE",
    }).then(fetchData);
  }
  const [filter, setFilter] = useState("all");

  const getTodosByFilter = () => {
    if (filter === "all") {
      return todos;
    }

    if (filter === "active") {
      return todos.filter((elem) => !elem.isDone);
    }

    if (filter === "finish") {
      return todos.filter((elem) => elem.isDone);
    }
  };
  const handleAddTodo = (inputValue, setInputValue) => {
    if (inputValue) {
      createNewTodo(inputValue);
      setInputValue("");
    } else {
      alert("Введите корректное значение");
    }
  };
  const listItemChange = (id) => {
    setTodos(
      todos.map((elem) => {
        if (elem.id === id) {
          elem.isDone = !elem.isDone;
          listItemChangeAPI(elem);
        }
        return elem;
      })
    );
  };

  return (
    <div className="App myApp">
      <Header />
      <TodoList
        deleteAllItemsAPI={deleteAllItemsAPI}
        deleteItemAPI={deleteItemAPI}
        handleAddTodo={handleAddTodo}
        listItemChange={listItemChange}
        filter={filter}
        setFilter={setFilter}
        getTodosByFilter={getTodosByFilter()}
        todos={todos}
        setTodos={setTodos}
      />
    </div>
  );
}

export default App;
