import {store} from "../../index";
import {v4 as uuidv4} from "uuid";
export const toggleTodoAction =(todo) => {
    return(store.dispatch({
        type: "TOGGLE_TODO",
        id: todo.id
    })
)
}
export const deleteTodoAction =(todo) => {
    return(store.dispatch({
            type: "DELETE_TODO",
            id: todo.id
        })
    )
}
export const addTodoAction =(inputValue) => ({
            type: 'ADD_TODO',
            id: uuidv4(),
            value: inputValue
        })
