import {SET_VISIBILITY_FILTER, SHOW_ALL} from "./types";
import {todos} from "../todos";

export const visibilityFilter = (state = SHOW_ALL, action) => {
    switch (action.type) {
        case SET_VISIBILITY_FILTER:
            return action.filter;
        default:
            return state;
    }
}



