import React from "react";
import BtnDelete from "./ButtonDelete";
import '../../index.css';

const ListItem = ({todo,setTodo,todos,id}) =>{
    const listItemChange = () =>{
        todos.map((user)=>{
            if (user.id ===id){
                user.isDone = !(user.isDone)
            }
        })
    }
    return(
        <li className={todo.isDone ? 'done' : 'notDone'} onClick={(e)=>{
            listItemChange()
        }}>
            {todo.value}
            <BtnDelete todos={todos} id={id} setTodo={setTodo} />
        </li>
    )
}
export default  ListItem;
