import React from "react";
import {deleteAllItems} from "../../services";
const ButtonFooter = ({ changeFilter, title, filter, filterExpValue,fetchData,deleteAllItems}) => {
  return (
    <button
      className={filterExpValue === filter ? "selectedButton" : "footerButton"}
      onClick={()=>{
        changeFilter()
          if(title==="Clear"){
              deleteAllItems().then(fetchData)
          }
      }}
    >
      {title}
    </button>
  );
};

export default ButtonFooter;
