import {ADD_TODO} from "../../constans";

export const tasks = (state = [], action) => {
    switch (action.type) {
        case ADD_TODO:
            return [
                ...state, {
                    id: action.id,
                    value: action.value,
                    isDone:action.isDone
                }
            ]
        default:
            return state
    }
}
