import React from "react";
import ButtonFooter from "./ButtonFooter";
import "../../index.css";
import {store} from "../../index";
const Footer = ({todos}) => {
    const filterChange = (filter) => store.dispatch({
        type:"SET_VISIBILITY_FILTER",
        filter
    })
    return (
        <footer className="footer">
            <ButtonFooter title="Clear"   onClick={() =>
                store.dispatch({
                    type:'DELETE_TODO',
                    id: todos.id
                })} />
            <ButtonFooter
                title="All"
                changeFilter={()=> filterChange('SHOW_ALL')}
            />
            <ButtonFooter
                title="Completed"
                changeFilter={()=> filterChange('SHOW_ACTIVE')}
            />
            <ButtonFooter
                title="In Progress"
                changeFilter={()=> filterChange('SHOW_COMPLETED')}
            />
        </footer>
    );
};

export default Footer;
