import React from "react";
import ButtonFooter from "./ButtonFooter";
import "../../index.css";
import {store} from "../../index";
const Footer = ({ setTodo, setFilter, filter }) => {
    const filterChange = (filter) => store.dispatch({
        type:"SET_VISIBILITY_FILTER",
        filter
    })
    return (
        <footer className="footer">
            <ButtonFooter title="Clear"   onClick={() => setTodo([])} />
            <ButtonFooter
                title="All"
                changeFilter={()=> filterChange('')}
            />
            <ButtonFooter
                title="Completed"
            />
            <ButtonFooter
                title="In Progress"
            />
        </footer>
    );
};

export default Footer;
