import React from "react";
import BtnDelete from "./btnDelete_comp";
const ListItem = ({todo,setTodo,todos,id}) =>{
    const ListItemChange = () =>{
        todo.isDone = !(todo.isDone)
        let obj = {
            id: todo.id,
            value: todo.value,
            isDone: !(todo.isDone)
        };
        const newTodos = todos.concat(obj)
        setTodo(newTodos)
    }
    return(
        <li onClick={()=>{
            ListItemChange()
            console.log(todo)
        }}>
            {todo.value}
            <BtnDelete todos={todos} id={id} setTodo={setTodo} />
        </li>
    )
}
export default  ListItem;
