import {todos} from "./states";

export const ADD_TODO ="ADD_TODO"

export const tasks = (state = todos, action) => {
    switch (action.type) {
        case ADD_TODO:
            return [
                ...state, {
                    id: action.id,
                    value: action.value,
                    isDone:action.isDone
                }
            ]
        default:
            return state
    }
}
