export const rootReducer = (state,action)  => {
    if (action.type=== 'INCREMENT'){
        console.log(`Add ${state}`)
        return state
    } else if(action.type === 'DECREMENT'){
        console.log(`Delete ${state}`)
        return state
    }
    return state
}
