import {ADD_TODO,DELETE_TODO,UPDATE_TODO} from "../constans"
import {todos} from "./states";


export const tasks = (state = todos, action) => {
    switch (action.type) {
        case ADD_TODO:
            return [
                ...state, {
                    id: action.id,
                    value: action.value,
                    isDone:action.isDone
                }
            ]
        default:
            return state
    }
}
