import React from "react";
import ListItem from "./ListItem";
import "../../index.css";
import {getVisibleTodos} from "../../redux/filter";

const List = ({ todos, filter}) => {
    const watchTodo =()=> getVisibleTodos(todos,filter)
    return (
      <ul className="list">
        {watchTodo.map((todo) => (
            <ListItem
                todo={todo}
                key={todo.id}
            />
        ))}
        <li className={todos.length === 0 ? "" : "hidden"}> No records found</li>
      </ul>
  );
};

export default List;
