import React from "react";
import ButtonFooter from "./ButtonFooter";
import "../../index.css";
import {SHOW_ACTIVE, SHOW_ALL,SHOW_COMPLETED} from "../../redux/filter/types";
import {useDispatch} from "react-redux";
import {deleteAllTodo} from "../../redux/todos/action";
import {changeFilterAction} from "../../redux/filter/action";

const Footer = ({todos}) => {
    const dispatch = useDispatch()

    return (
        <footer className="footer">
            <ButtonFooter title="Clear" onClick={() =>dispatch(deleteAllTodo(todos))}/>
            <ButtonFooter
                title="All"
                onClick={() => dispatch(changeFilterAction(SHOW_ALL))}
            />
            <ButtonFooter
                title="Completed"
                onClick={() => dispatch(changeFilterAction(SHOW_COMPLETED))}
            />
            <ButtonFooter
                title="In Progress"
                onClick={() => dispatch(changeFilterAction(SHOW_ACTIVE))}
            />
        </footer>
    );
};

export default Footer;
