import Header from "./components/todoList/Header";
import TodoList from "./components/todoList/TodoList";
import { useState } from "react";
import { v4 as uuidv4 } from "uuid";
import {store} from "./redux/main";

function App() {
  const [todos, setTodos] = useState(store.getState);
  const [filter, setFilter] = useState("all");
  console.log(store.getState())
  store.dispatch({type:'ADD_TODO',})
  const getTodosByFilter = () => {
    if (filter === "all") {
      return todos;
    }

    if (filter === "active") {
      return todos.filter((elem) => !elem.isDone);
    }

    if (filter === "finish") {
      return todos.filter((elem) => elem.isDone);
    }
  };
  const handleAddTodo = (inputValue, setInputValue) => {
    if (inputValue) {
      let obj = {
        id: uuidv4(),
        value: inputValue,
        isDone: false,
      };
      const newTodos = todos.concat(obj);
      setTodos(newTodos);
      setInputValue("");
    } else {
      alert("Введите корректное значение");
    }
  };
  const listItemChange = (id) => {
    setTodos(
        todos.map((elem) => {
          if (elem.id === id) {
            elem.isDone = !elem.isDone;
          }

          return elem;
        })
    );
  };

  return (
      <div className="App myApp">
        <Header />
        <TodoList
            handleAddTodo={handleAddTodo}
            listItemChange={listItemChange}
            filter={filter}
            setFilter={setFilter}
            getTodosByFilter={getTodosByFilter()}
            todos={todos}
            setTodos={setTodos}
        />
      </div>
  );
}

export default App;
